import React from "react";
import styled from "styled-components";
import { ListViewIcon, CardViewIcon, IconButton } from "@mozartfinance/uikit";
import { ViewMode } from "../types";

interface ToogleViewProps {
  viewMode: ViewMode;
  onToggle: (mode: ViewMode) => void;
}

const Container = styled.div`
  margin-left: -8px;

  ${({ theme }) => theme.mediaQueries.sm} {
    margin-left: 0;
  }
`;

const ToggleView: React.FunctionComponent<ToogleViewProps> = ({
  viewMode,
  onToggle,
}) => {
  const handleToggle = (mode: ViewMode) => {
    if (viewMode !== mode) {
      onToggle(mode);
    }
  };

  return (
    <Container>
      <IconButton
        variant="text"
        scale="sm"
        onClick={() => handleToggle(ViewMode.CARD)}
      >
        <CardViewIcon
          color={viewMode === ViewMode.CARD ? "primary" : "textDisabled"}
        />
      </IconButton>
      <IconButton
        variant="text"
        scale="sm"
        onClick={() => handleToggle(ViewMode.TABLE)}
      >
        <ListViewIcon
          color={viewMode === ViewMode.TABLE ? "primary" : "textDisabled"}
        />
      </IconButton>
    </Container>
  );
};

export default ToggleView;
