import React from 'react'
import styled from 'styled-components'
import { Heading, Text, BaseLayout } from '@mozartfinance/uikit'
import useI18n from 'hooks/useI18n'
import Page from 'components/layout/Page'
import FarmStakingCard from 'views/Home/components/FarmStakingCard'
import CakeStats from 'views/Home/components/CakeStats'
import TotalValueLockedCard from 'views/Home/components/TotalValueLockedCard'
import EarnAPYCard from 'views/Home/components/EarnAPYCard'
import EarnAssetCard from 'views/Home/components/EarnAssetCard'
import WinCard from 'views/Home/components/WinCard'

const Hero = styled.div`
  background-image: url('/images/saxophone-animated.svg');
  align-items: flex-start;
  background-repeat: no-repeat;
  background-position: left 80% center;
  display: flex;
  justify-content: center;
  flex-direction: column;
  margin: auto;
  margin-top: 10px;
  margin-left: 24px;
  margin-bottom: 32px;
  padding-top: 116px;
  text-align: center;
  height: 100px;
  padding-top: 0;
  background-size: contain;

  ${({ theme }) => theme.mediaQueries.lg} {
    background-image: url('/images/saxophone-animated.svg');
    background-position: right 40% center;
    align-items: flex-start;
    background-size: 50%;
    height: 100px;
    padding-top: 0;
    background-size: contain;
  }
`

const Cards = styled(BaseLayout)`
  align-items: stretch;
  justify-content: stretch;
  margin-bottom: 32px;

  & > div {
    grid-column: span 6;
    width: 100%;
  }

  ${({ theme }) => theme.mediaQueries.sm} {
    & > div {
      grid-column: span 8;
    }
  }

  ${({ theme }) => theme.mediaQueries.lg} {
    & > div {
      grid-column: span 6;
    }
  }
`

const SaxPic = styled.object`
  float: right;

`

const CTACards = styled(BaseLayout)`
  align-items: start;
  margin-bottom: 32px;

  & > div {
    grid-column: span 6;
  }

  ${({ theme }) => theme.mediaQueries.sm} {
    & > div {
      grid-column: span 8;
    }
  }

  ${({ theme }) => theme.mediaQueries.lg} {
    & > div {
      grid-column: span 4;
    }
  }
`

const Home: React.FC = () => {
  const TranslateString = useI18n()

  return (
    <Page>
      <Hero>
        <div>
          <Heading as="h1" size="xl" mb="24px" color="text" fontSize="80px">
            {TranslateString(576, 'Jazz DeFi')}
          </Heading>
          <Text>{TranslateString(578, "A DeFi project that's smooth.")}</Text>
          <SaxPic type="image/svg+xml" data="saxophone-animated.svg">
            <img alt="saxophone" src="saxophone-animated.svg" />
          </SaxPic>
        </div>
      </Hero>
      <div>
        <Cards>
          <FarmStakingCard />
        </Cards>
        <CTACards>
          <EarnAPYCard />
          <EarnAssetCard />
        </CTACards>
        <Cards>
          <CakeStats />
          <TotalValueLockedCard />
        </Cards>
      </div>
    </Page>
  )
}

export default Home
