import React from "react";
import { Button, Flex, Heading, useModal, Won } from "@mozartfinance/uikit";
import { useProfile } from "state/hooks";
import useI18n from "hooks/useI18n";
import ClaimNftAndCakeModal, { useCanClaim } from "./ClaimGiftModal";
import HeaderWrapper from "./HeaderWrapper";
import EditProfileModal from "./EditProfileModal";

const ProfileHeader = () => {
  const TranslateString = useI18n();
  const { canClaim, checkClaimStatus } = useCanClaim();
  const [onPresentClaimGiftModal] = useModal(
    <ClaimNftAndCakeModal onSuccess={checkClaimStatus} />
  );
  const [onEditProfileModal] = useModal(<EditProfileModal />, false);
  const { hasProfile } = useProfile();

  return (
    <HeaderWrapper>
      <Flex
        flexDirection={["column", null, "row"]}
        alignItems={["start", null, "center"]}
        justifyContent="space-between"
      >
        <div>
          <Heading as="h1" size="xxl" mb="8px" color="secondary">
            {TranslateString(999, "Your Profile")}
          </Heading>
          <Heading as="h2" size="lg" mb="16px">
            {TranslateString(999, "Check your stats and collect achievements")}
          </Heading>
          {hasProfile && (
            <Button onClick={onEditProfileModal}>
              {TranslateString(999, "Edit Profile")}
            </Button>
          )}
        </div>
        {canClaim && (
          <Button
            variant="tertiary"
            onClick={onPresentClaimGiftModal}
            startIcon={<Won />}
          >
            {TranslateString(999, "You've got a gift to claim!")}
          </Button>
        )}
      </Flex>
    </HeaderWrapper>
  );
};

export default ProfileHeader;
