import React from "react";
import BigNumber from "bignumber.js";
import styled from "styled-components";
import { Modal, Text, LinkExternal, Flex } from "@mozartfinance/uikit";
import useI18n from "hooks/useI18n";
import {
  calculateCakeEarnedPerThousandDollars,
  apyModalRoi,
} from "utils/compoundApyHelpers";

interface ApyCalculatorModalProps {
  onDismiss?: () => void;
  lpLabel?: string;
  cakePrice?: BigNumber;
  apy?: number;
  addLiquidityUrl?: string;
}

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: repeat(4, auto);
  margin-bottom: 24px;
`;

const GridItem = styled.div`
  margin-bottom: "10px";
`;

const Description = styled(Text)`
  max-width: 320px;
  margin-bottom: 28px;
`;

const ApyCalculatorModal: React.FC<ApyCalculatorModalProps> = ({
  onDismiss,
  lpLabel,
  cakePrice,
  apy,
  addLiquidityUrl,
}) => {
  const TranslateString = useI18n();
  const oneThousandDollarsWorthOfCake = 1000 / cakePrice.toNumber();

  const cakeEarnedPerThousand1D = calculateCakeEarnedPerThousandDollars({
    numberOfDays: 1,
    farmApy: apy,
    cakePrice,
  });
  const cakeEarnedPerThousand7D = calculateCakeEarnedPerThousandDollars({
    numberOfDays: 7,
    farmApy: apy,
    cakePrice,
  });
  const cakeEarnedPerThousand30D = calculateCakeEarnedPerThousandDollars({
    numberOfDays: 30,
    farmApy: apy,
    cakePrice,
  });
  const cakeEarnedPerThousand365D = calculateCakeEarnedPerThousandDollars({
    numberOfDays: 365,
    farmApy: apy,
    cakePrice,
  });

  return (
    <Modal title="ROI" onDismiss={onDismiss}>
      <Grid>
        <GridItem>
          <Text
            fontSize="12px"
            bold
            color="textSubtle"
            textTransform="uppercase"
            mb="20px"
          >
            {TranslateString(860, "Timeframe")}
          </Text>
        </GridItem>
        <GridItem>
          <Text
            fontSize="12px"
            bold
            color="textSubtle"
            textTransform="uppercase"
            mb="20px"
          >
            {TranslateString(858, "ROI")}
          </Text>
        </GridItem>
        <GridItem>
          <Text
            fontSize="12px"
            bold
            color="textSubtle"
            textTransform="uppercase"
            mb="20px"
          >
            {TranslateString(864, "JAZZ per $1000")}
          </Text>
        </GridItem>
        {/* 1 day row */}
        <GridItem>
          <Text>1d</Text>
        </GridItem>
        <GridItem>
          <Text>
            {apyModalRoi({
              amountEarned: cakeEarnedPerThousand1D,
              amountInvested: oneThousandDollarsWorthOfCake,
            })}
            %
          </Text>
        </GridItem>
        <GridItem>
          <Text>{cakeEarnedPerThousand1D}</Text>
        </GridItem>
        {/* 7 day row */}
        <GridItem>
          <Text>7d</Text>
        </GridItem>
        <GridItem>
          <Text>
            {apyModalRoi({
              amountEarned: cakeEarnedPerThousand7D,
              amountInvested: oneThousandDollarsWorthOfCake,
            })}
            %
          </Text>
        </GridItem>
        <GridItem>
          <Text>{cakeEarnedPerThousand7D}</Text>
        </GridItem>
        {/* 30 day row */}
        <GridItem>
          <Text>30d</Text>
        </GridItem>
        <GridItem>
          <Text>
            {apyModalRoi({
              amountEarned: cakeEarnedPerThousand30D,
              amountInvested: oneThousandDollarsWorthOfCake,
            })}
            %
          </Text>
        </GridItem>
        <GridItem>
          <Text>{cakeEarnedPerThousand30D}</Text>
        </GridItem>
        {/* 365 day / APY row */}
        <GridItem>
          <Text>365d(APY)</Text>
        </GridItem>
        <GridItem>
          <Text>
            {apyModalRoi({
              amountEarned: cakeEarnedPerThousand365D,
              amountInvested: oneThousandDollarsWorthOfCake,
            })}
            %
          </Text>
        </GridItem>
        <GridItem>
          <Text>{cakeEarnedPerThousand365D}</Text>
        </GridItem>
      </Grid>
      <Description fontSize="12px" color="textSubtle">
        {TranslateString(
          866,
          "Calculated based on current rates. Compounding once daily. Rates are estimates provided for your convenience only, and by no means represent guaranteed returns."
        )}
      </Description>
      <Flex justifyContent="center">
        <LinkExternal href={addLiquidityUrl}>
          {TranslateString(999, "Get")} {lpLabel}
        </LinkExternal>
      </Flex>
    </Modal>
  );
};

export default ApyCalculatorModal;
