import React from 'react'
import styled, { keyframes } from 'styled-components'
import { useFarmUser } from 'state/hooks'
import useI18n from 'hooks/useI18n'
import { Text, Image } from '@mozartfinance/uikit'
import { getBalanceNumber } from 'utils/formatBalance'

export interface FarmProps {
  label: string
  pid: number
  image: string
  depositFeeBP?: number
}

const IconImage = styled(Image)`
  width: 32px;
  height: 32px;
  backdrop-filter: blur(6px);
  ${({ theme }) => theme.mediaQueries.sm} {
    width: 64px;
    height: 64px;
    margin-right: 20px;
  }
`

const RainbowLight = keyframes`
	0% {
		background-position: 0% 50%;
	}
	50% {
		background-position: 100% 50%;
	}
	100% {
		background-position: 0% 50%;
	}
`

const Container = styled.div`
  padding-left: 16px;
  display: flex;
  align-items: center;

  ${({ theme }) => theme.mediaQueries.sm} {
    padding-left: 32px;
  }
`

const StyledCardAccent = styled.div`
div {
  background: linear-gradient( 
    45deg, 
    rgb(230, 198, 26) 0%, 
    rgb(255, 154, 0) 10%, 
    rgb(208, 222, 33) 20%, 
    rgb(204, 185, 29) 30%, 
    rgb(218, 179, 63) 40%, 
    rgb(226, 181, 47) 50%, 
    rgb(249, 241, 178) 60%, 
    rgb(242, 166, 21) 70%, 
    rgb(248, 222, 12) 80%, 
    rgb(251, 201, 7) 90%, 
    rgb(255, 179, 0) 100% 
  );
  background-size: 300% 300%;
  animation: ${RainbowLight} 2s linear infinite;
  border-radius: 16px;

  }
`

const Farm: React.FunctionComponent<FarmProps> = ({ image, label, pid, depositFeeBP }) => {
  const { stakedBalance } = useFarmUser(pid)
  const TranslateString = useI18n()
  const rawStakedBalance = getBalanceNumber(stakedBalance)
  const depositFeeColor = depositFeeBP / 100 === 0 ? 'rgb(50, 176, 123)' : '#1a1a1a'

  const handleRenderFarming = (): JSX.Element => {
    if (rawStakedBalance) {
      return (
        <Text color="secondary" fontSize="12px" bold>
          {TranslateString(999, 'FARMING')}
        </Text>
      )
    }

    return null
  }

  return (
    <Container>
      {label.split('-')[0] === 'JAZZ' ? <StyledCardAccent><IconImage src={`/images/farms/${image}.png`} alt="icon" width={64} height={64} mr="8px" /></StyledCardAccent> : <IconImage src={`/images/farms/${image}.png`} alt="icon" width={64} height={64} mr="8px" />}

      <div>
        {handleRenderFarming()}
        <Text bold>{label}</Text>
        <Text bold>
          Deposit Fee: <span style={{ color: depositFeeColor }}>{depositFeeBP / 100}%</span>
        </Text>
      </div>
    </Container>
  )
}

export default Farm
