import React from "react";
import styled from "styled-components";
import { Button, Flex, Input, InputProps } from "@mozartfinance/uikit";
import useI18n from "hooks/useI18n";

interface TokenInputProps extends InputProps {
  max: number | string;
  symbol: string;
  availableSymbol: string;
  value: string;
  onSelectMax?: () => void;
  onChange: (evt: React.FormEvent<HTMLInputElement>) => void;
}

const TicketInput: React.FC<TokenInputProps> = ({
  max,
  symbol,
  availableSymbol,
  onChange,
  onSelectMax,
  value,
}) => {
  const TranslateString = useI18n();

  return (
    <StyledTokenInput>
      <Flex alignItems="center">
        <Input onChange={onChange} placeholder="0" value={value} />
        <StyledTokenAdornmentWrapper>
          <StyledTokenSymbol>{symbol}</StyledTokenSymbol>
          <StyledSpacer />
          <div>
            <Button size="sm" onClick={onSelectMax}>
              {TranslateString(452, "Max")}
            </Button>
          </div>
        </StyledTokenAdornmentWrapper>
      </Flex>
      <StyledMaxText>
        {TranslateString(
          454,
          `${max.toLocaleString()} ${availableSymbol} Available`
        )}
      </StyledMaxText>
    </StyledTokenInput>
  );
};

const StyledTokenInput = styled.div``;

const StyledSpacer = styled.div`
  width: ${(props) => props.theme.spacing[3]}px;
`;

const StyledTokenAdornmentWrapper = styled.div`
  align-items: center;
  display: flex;
`;

const StyledMaxText = styled.div`
  align-items: center;
  color: ${(props) => props.theme.colors.primary};
  display: flex;
  font-size: 14px;
  font-weight: 700;
  height: 44px;
  justify-content: flex-end;
`;

const StyledTokenSymbol = styled.span`
  color: ${(props) => props.theme.colors.primary};
  font-weight: 700;
`;

export default TicketInput;
